import Link from 'next/link'

export default class Clips extends React.Component {
  render(){
    const { arr } = this.props

    return(
      <React.Fragment>
        { arr.map((clip) => (
          <Link href={`/podcast?id=${ clip.id }`} key={ clip.id }>
            <a className="channel">
              <li>
                <img src={ clip.urls.image || clip.channel.urls.logo_image.original } alt={ clip.title } />
                <h4> { clip.title } </h4>
              </li>
            </a>
          </Link>
        )) }

        <style jsx>{`
            header{
              color: #fff;
              background: #8756ca;
              padding: 15px;
              font-size: 1.5em;
              font-weight: bold;
              text-align: center;
            }
            .channels {
              display: grid;
              grid-gap: 15px;
              padding: 15px;
              grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
            }
            .channel {
              display: block;
              border-radius: 3px;
              box-shadow 0px 2px 6px rgba(0,0,0,0.15);
              background-color: white;
              color: black;
              margin-bottom: 0.5em;
              text-align: center;
            }
            .channel img {
              width: 100%;
            }
            h1 {
              text-align: center;
              font-weight: 600;
              padding: 15px;
            }
            h2 {
              padding: 5px;
              font-size: 0.9em;
              font-weigth: 600;
              margin: 0;
              color: whitesmoke;
              text-align: center;
            }
            h4{
              color: black;
            }
            .hero{
              height: 250px;
              width: 100%;
              background-position: center;
            }
          `}</style>
      </React.Fragment>
    )
  }
}
