import Link from 'next/link'

export default class extends React.Component {

  static async getInitialProps({ query }){
    let idChannel = query.id

    let [reqChannel, reqAudios, reqSeries] = await Promise.all([
      fetch(`https://api.audioboom.com/channels/${idChannel}`),
      fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`),
      fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`)
    ])

    let dataChannel = await reqChannel.json()
    let channel = dataChannel.body.channel

    let dataAudios = await reqAudios.json()
    let audioClips = dataAudios.body.audio_clips

    let dataSeries = await reqSeries.json()
    let series = dataSeries.body.channels

    return { channel, audioClips, series, idChannel }
  }
  render() {
  
    const { channel, audioClips, series, idChannel } = this.props

    return(
      <div>
        <Link href={`/`}>
          <a>
            <header>Podcast</header>
          </a>
        </Link>
        <h1>{ channel.title }</h1>
        <div className="hero"  ></div>

        <h2>Series</h2>
        <ul>
          { series.map((serie) => (
            <li key={ serie.id } >
              { serie.title }
            </li>
          )) }
        </ul>

        <h2>Ultimos Clips</h2>
        <ul className="channels">
          { audioClips.map((clip) => (
            <Link href={`/podcast?id=${ clip.id }`} key={ clip.id }>
              <a className="channel">
                <li>
                  <img src={ clip.urls.image || clip.channel.urls.logo_image.original } alt={ clip.title } />
                  <h4> { clip.title } </h4>
                </li>
              </a>
            </Link>
          )) }
        </ul>

        <style jsx>{`
          header{
            color: #fff;
            background: #8756ca;
            padding: 15px;
            font-size: 1.5em;
            font-weight: bold;
            text-align: center;
          }
          .channels {
            display: grid;
            grid-gap: 15px;
            padding: 15px;
            grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
          }
          .channel {
            display: block;
            border-radius: 3px;
            box-shadow 0px 2px 6px rgba(0,0,0,0.15);
            margin-bottom: 0.5em;
            background-color: whitesmoke;
            text-align: center;
          }
          .channel img {
            width: 100%;
          }
          h1 {
            text-align: center;
            font-weight: 600;
            padding: 15px;
          }
          h2 {
            padding: 5px;
            font-size: 0.9em;
            font-weigth: 600;
            margin: 0;
            color: whitesmoke;
            text-align: center;
          }
          h4{
            color: black;
          }
        `}</style>

        <style jsx global>{`
          body{
            margin: 0;
            font-family: system-ui;
            background-color: #234050;
          }
          a{
            color: whitesmoke;
            text-decoration: none;
          }
          li{
            list-style: none;
            color: whitesmoke;
          }
        `}</style>
      </div>
    )
  }
}
