import { withRouter } from 'next/router'

export default class Podacst extends React.Component{
  static async getInitialProps(){
    const { queryId } = this.props

    let req = await fetch(`https://api.audioboom.com/channels/${queryId}/audio_clips`)
    let data = await req.json()
    let collection = data.body.audio_clips

    return collection
  }

  render() {
    const { altTitle, audioClip, clipImg, collection } = this.props

    return(
      <React.Fragment>
        <div className="podcast-container">
            <img src={ clipImg } alt={ altTitle } className="hero" />
            <audio controls autoPlay>
              <source src={ audioClip } type="audio/mpeg" />
            </audio>
        </div>

          <style jsx>{`
          .hero{
              width: 250px;
              height: 250px;
            }
            .podcast-container{
              height: 350px;
              display: flex;
              flex-direction: column;
              align-items: center;
              justify-content: space-between;
            }
            audio{
              outline: 0;
            }
          `}</style>
      </React.Fragment>
    )
  }
}
