export default class Series extends React.Component {
  render() {
    const { arr } = this.props

    return(
      <React.Fragment>
        <h2>Series</h2>
        { arr.length > 0 &&(
              arr.map((item) => (
              <li key={ item.id } >
                { item.title }
              </li>
              ))
            )
          }
      </React.Fragment>
    )
  }
}
