export default class Series extends React.Component {
  render() {
    const { arr } = this.props
    
    return(
      <React.Fragment>
        <h2>Series</h2>
        <ul className="series-list-container">
          {arr.map((item) => (
              <li className="series-list-item" key={ item.id } >
                <img className="item-image" src={item.urls.banner_image.original} alt=""/>
                <p>
                  <strong> { `${ item.title }:` } </ strong>
                  { `${item.formatted_description}` }
                </p>
              </li>
            ))
          }
        </ul>

        <style jsx> {`
          .series-list-item {
            height: 55px;
            font-size: 15px;
            display: flex;
            color: black;
            background-color: #fff;
            align-items: center;
            }
            .series-list-item p {
              display: flex;
              flex-direction: column;
            }
            
          `}</style>
          <style jsx>{`
            header{
              color: #fff;
              background: #8756ca;
              padding: 15px;
              font-size: 1.5em;
              font-weight: bold;
              text-align: center;
            }
            .channels {
              display: grid;
              grid-gap: 15px;
              padding: 15px;
              grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
            }
            .channel {
              display: block;
              border-radius: 3px;
              box-shadow 0px 2px 6px rgba(0,0,0,0.15);
              background-color: white;
              color: black;
              margin-bottom: 0.5em;
              text-align: center;
            }
            .channel img {
              width: 100%;
            }
            h1 {
              text-align: center;
              font-weight: 600;
              padding: 15px;
            }
            h2 {
              padding: 5px;
              font-size: 0.9em;
              font-weigth: 600;
              margin: 0;
              color: whitesmoke;
              text-align: center;
            }
            h4{
              color: black;
            }
            .item-image {
              height: 55px;
              width: 85px;
            }
          `}</style>
      </React.Fragment>
    )
  }
}
