import Link from 'next/link'
import Head from 'net/head'

export default class Layout extends React.Component {
  render(){
    const { children, title } = this.props

    return(
      <React.Fragment>
        <Head>
          <title> { title } </title>
        </Head>
        
        <header>
          <Link to="/">
            <a>Podcast</a>
          </Link>
            { children }
        </header>

        <style jsx>{`
          header{
            color: #fff;
            background: #8756ca;
            padding: 15px;
            font-size: 1.5em;
            font-weight: bold;
            text-align: center;
          }
          header a{
            color: #fff;
          }
        `}</style>
        
        <style jsx global>{`
          body{
            margin: 0;
            font-family: system-ui;
            background-color: #234050;
          }
          a{
            text-decoration: none;
          }
        `}</style>
      </React.Fragment>
    )
  }
}
