export default class Series extends React.Component {
  render() {
    const { arr } = this.props

    return(
      <React.Fragment>
        {arr.map((item) => (
            <li className="series-list-item" key={ item.id } >
              {`${ item.title }`}
            </li>
          ))
        }

        <style jsx> {
          `.series-list-item{
              color: black;
              background-color: #fff;
            }
          `
        } </style>
      </React.Fragment>
    )
  }
}
