export default class Series extends React.Component {
  render() {
    const { arr } = this.props

    return(
      <React.Fragment>
        {arr.map((item) => (
            <li key={ item.id } >
              {`${ item.title } <br /> ${ item.updated_at } `}
            </li>
          ))
        }
      </React.Fragment>
    )
  }
}
