import { withRouter } from 'next/router'
import Link from 'next/link'

import Layout from '../component/Layout'

export default class extends React.Component{
  static async getInitialProps({ query }){
    const podcastId = query.id

    let req = await fetch(`https://api.audioboom.com/audio_clips/${podcastId}}`)
    let data = await req.json()
    let audioClip = data.body.audio_clip

    return audioClip
  }

  render(){
    const audioClip = this.props

    return(
      <Layout>

          <h1>{ audioClip.title }</h1>

          <div className="podcast-container">
            <img src={ audioClip.urls.image || audioClip.channel.urls.logo_image.original } alt={audioClip.title} className="hero" />
            <audio controls autoPlay>
              <source src={ audioClip.urls.high_mp3 } type="audio/mpeg" />
            </audio>
          </div>

          <style jsx>{`
            header{
              color: #fff;
              background: #8756ca;
              padding: 15px;
              font-size: 1.5em;
              font-weight: bold;
              text-align: center;
            }
            h1{
              text-align: center;
              color: whitesmoke;
            }
            .channels {
              display: grid;
              grid-gap: 15px;
              padding: 15px;
              grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
            }
            .channel {
              display: block;
              border-radius: 3px;
              box-shadow 0px 2px 6px rgba(0,0,0,0.15);
              margin-bottom: 0.5em;
            }
            .hero{
              width: 250px;
              height: 250px;
            }
            .podcast-container{
              height: 350px;
              display: flex;
              flex-direction: column;
              align-items: center;
              justify-content: space-between;
            }
            audio{
              outline: 0;
            }
          `}</style>

          <style jsx global>{`
            body{
              margin: 0;
              font-family: system-ui;
              background-color: #234050;
            }
            a{
              text-decoration: none;
            }
          `}</style>
      </Layout>
    )
  }
}
