import Link from 'next/link'
import Podcast from '../component/Podcast'

import Layout from '../component/Layout'

export default class extends React.Component{
  static async getInitialProps({ query }){
    const podcastId = query.id

    let  [reqClip, reqLatest] = await Promise.all([
      fetch(`https://api.audioboom.com/audio_clips/${podcastId}}`),
      fetch(`https://api.audioboom.com/channels/${this.props.channel.id}/audio_clips`)
    ])

    let data = await reqClip.json()
    let audioClip = data.body.audio_clip

    let dataLate = await reqLatest.json()
    let latest_clips = data.body.audio_clip

    return { audioClip, latest_clips }
  }

  render(){
    const audioClip = this.props

    return(
      <Layout title={ audioClip.title } >

          <h1>{ audioClip.title }</h1>

          <Podcast 
          clipImg={ audioClip.urls.image || audioClip.channel.urls.logo_image.original }
          altTitle={ audioClip.title }
          audioClip={ audioClip.urls.high_mp3 }
          />

          <style jsx>{`
            h1{
              text-align: center;
              color: whitesmoke;
            }
          `}</style>
      </Layout>
    )
  }
}
