export default class extends React.Component {
  render() {
    return (
      <div>
        <img src="/static/platzi-logo.png" alt="Platzi Logo"/>

        <h1>Hola Mundo!</h1>
        <p>Bienvenidos todos al curso de Next.Js</p>

        <style jxk>{`
          h1 {
            color: red;
          }
          :global(div p){
            color: green;
          }
          img {
            max-width: 50%;
            display: block;
            margin: 0 auto;
          }
        `}</style>

        <style jsx global>{`
          body{
            background-color: #234050;
          }
        `}</style>
      </div>
    )
  }
}
