import Link from '../routes'

export default class PodacastPlayer extends React.Component {
  render() {
    // const { altTitle, audioClip, clipImg, box } = this.props
    const { clip, onClose } = this.props

    return(
      <React.Fragment>
        <nav>
          <Link href={`channel?id=${clip.channel.id}`} >
            <a onClick={ onClose } ></a>
          </Link>
        </nav>

        <div className="podcast-container">
            <img src={ clip.urls.image || clip.channel.urls.logo_image.original }
            alt="sub title" className="hero" />
            <audio controls autoPlay>
              {/* <source src={  } type="audio/mpeg" /> */}
            </audio>
            <p>
              {/* { `comments: ${box.comments} likes: ${box.likes} plays: ${box.plays}` } */}
            </p>
        </div>

          <style jsx>{`
          .hero{
              background-color: black;
              width: 250px;
              height: 250px;
            }
            .podcast-container{
              height: 350px;
              display: flex;
              flex-direction: column;
              align-items: center;
              justify-content: space-between;
            }
            audio{
              outline: 0;
            }
            p{
              color: whitesmoke;
            }
          `}</style>
      </React.Fragment>
    )
  }
}
