export default class Podacst extends React.Component{
  render() {
    const { altTitle } = this.props

    return(
      <React.Fragment>
        <div className="podcast-container">
            <img src={ audioClip.urls.image || audioClip.channel.urls.logo_image.original } alt={ altTitle } className="hero" />
            <audio controls autoPlay>
              <source src={ audioClip } type="audio/mpeg" />
            </audio>
          </div>
      </React.Fragment>
    )
  }
}
